import React, { useEffect, useState, useRef } from 'react';
import API from '../api';
import { Link } from 'react-router-dom';

function useScrollFade(){
  useEffect(()=>{
    const obs = new IntersectionObserver(entries=>{
      entries.forEach(e=>{ if(e.isIntersecting) e.target.classList.add('show'); });
    }, {threshold:0.15});
    document.querySelectorAll('.fade-in').forEach(el=>obs.observe(el));
    return ()=>obs.disconnect();
  }, []);
}

export default function Home(){
  const [jobs, setJobs] = useState([]);
  const heroRef = useRef();
  useScrollFade();

  useEffect(()=>{ API.get('/jobs').then(res=>setJobs(res.data)).catch(()=>{}); }, []);

  // simple tilt effect for cards
  const onMove = (e) => {
    const card = e.currentTarget;
    const rect = card.getBoundingClientRect();
    const x = (e.clientX - rect.left) / rect.width - 0.5;
    const y = (e.clientY - rect.top) / rect.height - 0.5;
    card.style.transform = `perspective(800px) rotateX(${ -y * 6 }deg) rotateY(${ x * 8 }deg) translateY(-6px)`;
  };
  const onLeave = (e)=>{ e.currentTarget.style.transform = ''; };

  return (
    <div>
      <section className="hero-bg" style={{padding:'3rem 0'}}>
        <div className="hero-anim" aria-hidden="true"></div>
        <div className="container container-smash hero-content">
          <div className="glass p-4 d-flex align-items-center justify-content-between">
            <div>
              <h1 className="mb-1">Find your next opportunity</h1>
              <p className="mb-0 text-muted">Search real jobs from real employers — beautifully designed, simply delivered.</p>
            </div>
            <div style={{minWidth:320}}>
              <div className="input-group">
                <input className="form-control form-glass" placeholder="Search jobs, e.g., Frontend Developer" />
                <button className="btn btn-glass">Search</button>
              </div>
            </div>
          </div>
        </div>
      </section>

      <section className="container container-smash mt-4">
        <h4 className="mb-3">Available jobs</h4>
        <div className="row">
          {jobs.map(job=>(
            <div key={job.id} className="col-md-4 mb-4 fade-in">
              <div className="glass job-card-inner job-card" onMouseMove={onMove} onMouseLeave={onLeave}>
                <h5>{job.title}</h5>
                <p className="text-muted">{job.company} • {job.location}</p>
                <p style={{minHeight:48}}>{job.description?.slice(0,120)}{job.description?.length>120?'...':''}</p>
                <div className="d-flex justify-content-between align-items-center">
                  <Link to={`/applications/${job.id}`} className="btn btn-sm btn-glass">Apply / View</Link>
                  <small className="text-muted">{new Date(job.createdAt).toLocaleDateString()}</small>
                </div>
              </div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}
