import React from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import { AuthProvider } from './contexts/AuthContext';
import Navbar from './components/Navbar';
import Home from './pages/Home';
import Login from './pages/Login';
import Register from './pages/Register';
import JobListing from './pages/JobListing';
import JobDetails from './pages/JobDetails';
import ApplicationForm from './pages/ApplicationForm';
import ApplicantDashboard from './pages/ApplicantDashboard';
import EmployerDashboard from './pages/EmployerDashboard';
import PostJob from './pages/PostJob';
import ManageJobs from './pages/ManageJobs';
import ViewApplications from './pages/ViewApplications';
import About from './pages/About';
import Contact from './pages/Contact';
import PrivacyPolicy from './pages/PrivacyPolicy';
import TermsOfService from './pages/TermsOfService';
import Profile from './pages/Profile';

function App() {
  return (
    <AuthProvider>
      <BrowserRouter>
        <Navbar />
        <div style={{minHeight: 'calc(100vh - 300px)'}}>
          <Routes>
            <Route path='/' element={<Home />} />
            <Route path='/login' element={<Login />} />
            <Route path='/register' element={<Register />} />
            <Route path='/jobs' element={<JobListing />} />
            <Route path='/jobs/:id' element={<JobDetails />} />
            <Route path='/apply/:jobId' element={<ApplicationForm />} />
            <Route path='/dashboard' element={<ApplicantDashboard />} />
            <Route path='/employer/dashboard' element={<EmployerDashboard />} />
            <Route path='/post-job' element={<PostJob />} />
            <Route path='/manage-jobs' element={<ManageJobs />} />
            <Route path='/applications/:jobId' element={<ViewApplications />} />
            <Route path='/profile' element={<Profile />} />
            <Route path='/about' element={<About />} />
            <Route path='/contact' element={<Contact />} />
            <Route path='/privacy-policy' element={<PrivacyPolicy />} />
            <Route path='/terms-of-service' element={<TermsOfService />} />
          </Routes>
        </div>
        <footer className="footer-glass py-4">
          <div className="container-smash">
            <div className="row text-center text-md-start">
              <div className="col-md-4 mb-4 mb-md-0">
                <h6 className="fw-bold mb-3">Online Job Portal</h6>
                <p className="small mb-0" style={{opacity: 0.7}}>Find your dream job or hire the best talent</p>
              </div>
              <div className="col-md-4 mb-4 mb-md-0">
                <h6 className="fw-bold mb-3">Quick Links</h6>
                <div className="d-flex flex-column small">
                  <Link to="/about" className="text-decoration-none mb-2" style={{opacity: 0.7}}>About Us</Link>
                  <Link to="/contact" className="text-decoration-none mb-2" style={{opacity: 0.7}}>Contact</Link>
                  <Link to="/privacy-policy" className="text-decoration-none mb-2" style={{opacity: 0.7}}>Privacy Policy</Link>
                  <Link to="/terms-of-service" className="text-decoration-none" style={{opacity: 0.7}}>Terms of Service</Link>
                </div>
              </div>
              <div className="col-md-4">
                <h6 className="fw-bold mb-3">Connect With Us</h6>
                <div className="d-flex justify-content-center justify-content-md-start gap-3">
                  <a href="#" className="text-decoration-none" style={{fontSize: '1.5rem'}}>üê¶</a>
                  <a href="#" className="text-decoration-none" style={{fontSize: '1.5rem'}}>üíº</a>
                  <a href="#" className="text-decoration-none" style={{fontSize: '1.5rem'}}>üìò</a>
                </div>
              </div>
            </div>
            <hr className="my-4" style={{opacity: 0.2}} />
            <div className="text-center">
              <small style={{opacity: 0.6}}>¬© {new Date().getFullYear()} Online Job Portal. All rights reserved.</small>
            </div>
          </div>
        </footer>
      </BrowserRouter>
    </AuthProvider>
  );
}

export default App;
