import React, { useState } from 'react';
import API from '../api';
import { useNavigate } from 'react-router-dom';

export default function Login(){
  const [form, setForm] = useState({ email:'', password:'' });
  const navigate = useNavigate();
  const submit = async (e) => {
    e.preventDefault();
    try {
      const res = await API.post('/auth/login', form);
      localStorage.setItem('ojp_token', res.data.token);
      localStorage.setItem('ojp_user', JSON.stringify(res.data.user));
      navigate('/');
    } catch (err) {
      alert(err.response?.data?.error || 'Login failed');
    }
  };
  return (
    <div className="container container-smash mt-5">
      <div className="glass p-4" style={{maxWidth:560, margin:'0 auto'}}>
        <h3>Sign in</h3>
        <form onSubmit={submit}>
          <input className="form-control mb-3" type="email" placeholder="Email" value={form.email} onChange={e=>setForm({...form,email:e.target.value})} required />
          <input className="form-control mb-3" type="password" placeholder="Password" value={form.password} onChange={e=>setForm({...form,password:e.target.value})} required />
          <button className="btn btn-glass">Login</button>
        </form>
      </div>
    </div>
  );
}
