import React, { useState } from 'react';
import API from '../api';
import { useNavigate } from 'react-router-dom';

export default function PostJob(){
  const [form, setForm] = useState({ title:'', description:'', company:'', location:'', salary:'' });
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('ojp_user') || 'null');

  const submit = async (e) => {
    e.preventDefault();
    if (!user || user.role !== 'employer') return alert('Please login as employer to post a job');
    try {
      await API.post('/jobs', form);
      alert('Job posted');
      navigate('/');
    } catch (err) {
      alert(err.response?.data?.error || 'Failed');
    }
  };

  return (
    <div className="container container-smash mt-5">
      <div className="glass p-4" style={{maxWidth:760, margin:'0 auto'}}>
        <h3>Post a Job</h3>
        <form onSubmit={submit}>
          <input className="form-control mb-3" placeholder="Job title" value={form.title} onChange={e=>setForm({...form,title:e.target.value})} required />
          <input className="form-control mb-3" placeholder="Company" value={form.company} onChange={e=>setForm({...form,company:e.target.value})} required />
          <input className="form-control mb-3" placeholder="Location" value={form.location} onChange={e=>setForm({...form,location:e.target.value})} />
          <input className="form-control mb-3" placeholder="Salary" value={form.salary} onChange={e=>setForm({...form,salary:e.target.value})} />
          <textarea className="form-control mb-3" rows="6" placeholder="Description" value={form.description} onChange={e=>setForm({...form,description:e.target.value})} />
          <button className="btn btn-glass">Publish Job</button>
        </form>
      </div>
    </div>
  );
}
