import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

export default function Navbar(){
  const navigate = useNavigate();
  const userStr = localStorage.getItem('ojp_user');
  const user = userStr ? JSON.parse(userStr) : null;

  const logout = ()=>{
    localStorage.removeItem('ojp_token');
    localStorage.removeItem('ojp_user');
    navigate('/login');
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-glass">
      <div className="container container-smash">
        <Link className="navbar-brand fw-semibold" to="/">Job Portal</Link>
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navMenu">
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navMenu">
          <ul className="navbar-nav ms-auto align-items-center">
            <li className="nav-item"><Link className="nav-link" to="/about">About</Link></li>
            <li className="nav-item"><Link className="nav-link" to="/contact">Contact</Link></li>
            {!user && <li className="nav-item"><Link className="nav-link" to="/login">Login</Link></li>}
            {!user && <li className="nav-item"><Link className="nav-link" to="/register">Register</Link></li>}
            {user && user.role === 'employer' && <li className="nav-item"><Link className="nav-link btn btn-sm btn-glass ms-2" to="/post-job">Post Job</Link></li>}
            {user && <li className="nav-item"><button className="btn btn-sm btn-glass ms-2" onClick={logout}>Logout</button></li>}
          </ul>
        </div>
      </div>
    </nav>
  );
}
