import React, { useState } from 'react';

export default function Contact(){
  const [form, setForm] = useState({ name:'', email:'', message:'' });
  const submit = (e)=>{ e.preventDefault(); console.log('Contact form', form); alert('Message sent (demo)'); setForm({ name:'', email:'', message:'' }); };

  return (
    <div className="container container-smash mt-5">
      <div className="glass p-4">
        <h3>Contact Us</h3>
        <p className="text-muted">Have questions? Send us a message â€” this demo form logs the data to the console.</p>
        <form onSubmit={submit} style={{maxWidth:700}}>
          <input className="form-control mb-3" placeholder="Name" value={form.name} onChange={e=>setForm({...form,name:e.target.value})} required />
          <input className="form-control mb-3" placeholder="Email" value={form.email} onChange={e=>setForm({...form,email:e.target.value})} required />
          <textarea className="form-control mb-3" rows="5" placeholder="Message" value={form.message} onChange={e=>setForm({...form,message:e.target.value})} required />
          <button className="btn btn-glass w-100" type="submit">Send Message</button>
        </form>
      </div>
    </div>
  );
}
