import React, { useState } from 'react';
import API from '../api';
import { useNavigate } from 'react-router-dom';

export default function Register(){
  const [form, setForm] = useState({ name:'', email:'', password:'', role:'seeker' });
  const navigate = useNavigate();
  const submit = async (e) => {
    e.preventDefault();
    try {
      const res = await API.post('/auth/register', form);
      localStorage.setItem('ojp_token', res.data.token);
      localStorage.setItem('ojp_user', JSON.stringify(res.data.user));
      navigate('/');
    } catch (err) {
      alert(err.response?.data?.error || 'Registration failed');
    }
  };
  return (
    <div className="container container-smash mt-5">
      <div className="glass p-4" style={{maxWidth:720, margin:'0 auto'}}>
        <h3>Create an account</h3>
        <form onSubmit={submit}>
          <input className="form-control mb-3" placeholder="Full name" value={form.name} onChange={e=>setForm({...form,name:e.target.value})} required />
          <input className="form-control mb-3" type="email" placeholder="Email" value={form.email} onChange={e=>setForm({...form,email:e.target.value})} required />
          <input className="form-control mb-3" type="password" placeholder="Password" value={form.password} onChange={e=>setForm({...form,password:e.target.value})} required />
          <select className="form-select mb-3" value={form.role} onChange={e=>setForm({...form,role:e.target.value})}>
            <option value="seeker">Job Seeker</option>
            <option value="employer">Employer</option>
          </select>
          <button className="btn btn-glass">Register</button>
        </form>
      </div>
    </div>
  );
}
